mapper.update:
update user_passenger

    passenger_phonenumber = #{passengerDTO.phonenumber},

where user_id = #{passengerDTO.userId} and passenger_id = #{passengerId}

mapper.delete:
delete from user_passenger where passenger_id in

    #{id}

AND user_id = #{userId}

mapper.select:
SELECT COUNT(*) > 0 AS association_exists
FROM user_passenger
WHERE user_id = #{userId} AND passenger_id = #{passengerId}
